import React, {useState, useEffect} from 'react';
function App() {
  const [imags, setImags] = useState(false);
  useEffect(() => {
    getImags();
  }, []);
  function getImags() {
    fetch('http://localhost:3001')
      .then(response => {
        return response.text();
      })
      .then(data => {
        setImags(data);
      });
  }
  function createImags() {
    let bibNumber = prompt('Enter bib number');
    let imagName = prompt('Enter imag name');
    let eventName = prompt('Enter event name');
    fetch('http://localhost:3001/imags', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({bibNumber, imagName, eventName}),
    })
      .then(response => {
        return response.text();
      })
      .then(data => {
        alert(data);
        getImags();
      });
  }
  function deleteImags() {
    let bibNumber = prompt('Enter bib number');
    fetch(`http://localhost:3001/imags/${bibNumber}`, {
      method: 'DELETE',
    })
      .then(response => {
        return response.text();
      })
      .then(data => {
        alert(data);
        getImags();
      });
  }
  return (
    <div>
      {imags ? imags : 'There is no imags data available'}
      <br />
      <button onClick={createImags}>Add imag</button>
      <br />
      <button onClick={deleteImags}>Delete imag</button>
    </div>
  );
}
export default App;
